import React, { useEffect, useState } from 'react';
import ReactDOM from 'react-dom';
import styled from 'styled-components'


function App() {
  let [posts, setPosts] = useState([]);

  useEffect(() => {
    fetch('https://jsonplaceholder.typicode.com/posts')
      .then(res => res.json())
      .then(postsRes => setPosts(postsRes));
  }, []);

  return <MainStyle>
  <Title>THIS IS A WEB-CHAT APP</Title>
  <Panes>
    <Pane width={'35%'} minWidth={'300px'}>
      <Posts posts={posts}></Posts>
    </Pane>
    <Pane width={'65%'}>
      here be comments
    </Pane>
  </Panes>
  </MainStyle>;
}

function Posts(props) {
  let postStyle = {
    border: '1px solid',
    padding: '0.5em',
    cursor: 'pointer'
  }
  return <ul>
    {props.posts.map(post => {
      return <div style={postStyle} key={post.id}>
        {post.title} ({post.id})
      </div>
    })}
  </ul>
}

function Panes(props) {
  let mainStyle = {
    display: 'flex',
    width: '100vw',
    height: '100vh'
  };
  return <main style={mainStyle}>{props.children}</main>
}
const MainStyle = styled.div({
  display:'flex'
})

function Pane(props) {
  let paneStyle = {
    overflow: 'auto',
    ...props
  }
  return <div style={paneStyle}>{props.children}</div>
}

const Title = styled.h1({
  display:'flex',
  alignItems:'center',
  fontSize: '1.5em',
  textAlign: 'center',
  color: 'palevioletred'
});

ReactDOM.render(
  <App />,
  document.getElementById('root')
);
