import React, { useEffect, useState } from 'react';
import styled from 'styled-components/macro';
import { Posts } from './Posts.jsx'

export function App() {
    let [posts, setPosts] = useState([]);
    /*
    useEffect(() => {
      fetch('https://jsonplaceholder.typicode.com/posts')
        .then(res => res.json())
        .then(postsRes => setPosts(postsRes));
    }, []);
    */

    useEffect(() => {
      import('./data/posts.js')
      .then(module => {
        setPosts(module.posts);
      });
    })
    return <MainStyle>
    <Title>THIS IS A WEB-CHAT APP</Title>
      <Panes>
        <Pane width={'35%'} minWidth={'300px'}>
          <Posts posts={posts}></Posts>
        </Pane>
        <Pane width={'65%'}>
          here be comments
        </Pane>
      </Panes>
    </MainStyle>;
  }

export function Panes(props) {
    let mainStyle = {
      display: 'flex',
      width: '100vw',
      height: '100vh'
    };
    return <main style={mainStyle}>{props.children}</main>
  }


export const MainStyle = styled.div({
    display:'flex'
  })

export function Pane(props) {
    let paneStyle = {
      overflow: 'auto',
      ...props
    }
    return <div style={paneStyle}>{props.children}</div>
  }

export const Title = styled.h1({
    display:'inline',
    maxWidth:'100%',
    fontSize: '1.5em',
    textAlign: 'center',
    color: 'palevioletred'
  });
