export interface SecurityLocalization {
    /**
     * Security Policy page strings
     */
    security: {
        /**
         * Security policy settings page title
         */
        pageTitle: string;
        /**
         * Security subtitle for settings section
         */
        subtitle: string;
        /**
         * MFA settings row title
         */
        mfaTitle: string;
        /**
         * MFA policy text if policy is 'DontForce'
         */
        mfaPolicyDontForceDescription: string;
        /**
         * MFA policy text if policy is 'Force'
         */
        mfaPolicyForceDescription: string;
        /**
         * MFA policy text if policy is 'ForceExceptSAML'
         */
        mfaPolicyForceExceptSAMLDescription: string;
        /**
         * Lockout settings row title
         */
        lockoutTitle: string;
        /**
         * Lockout status if policy not configured
         */
        lockoutNotConfigured: string;
        /**
         * Lockout attempts placeholder BEFORE "attempts" value
         */
        lockoutEnabledMaxAttempts1: string;
        /**
         * Lockout attempts placeholder After "attempts" value
         */
        lockoutEnabledMaxAttempts2: string;
        /**
         * Lockout status if policy is disabled
         */
        lockoutDisabled: string;
        /**
         * Password history settings row title
         */
        passwordHistoryTitle: string;
        /**
         * Password History status if policy not configured
         */
        passwordHistoryNotConfigured: string;
        /**
         * Password History attempts placeholder BEFORE "attempts" value
         */
        passwordHistoryEnabledHistorySize1: string;
        /**
         * Password History attempts placeholder After "attempts" value
         */
        passwordHistoryEnabledHistorySize2: string;
        /**
         * Password History status if policy is disabled
         */
        passwordHistoryDisabled: string;
    };
    /**
     * Edit MFA security policy dialog strings
     */
    security_EditMfaPolicy: {
        /**
         * MFA Policy Dialog title
         */
        title: string;
        /**
         * Don't force option title
         */
        dontForceTitle: string;
        /**
         * Don't force option description
         */
        dontForceDescription: string;
        /**
         * Force mfa option title
         */
        forceTitle: string;
        /**
         * Force mfa option description
         */
        forceDescription: string;
        /**
         * Force mfa 'for all users except SAML logged in' option title
         */
        forceExceptSAMLTitle: string;
        /**
         * Force mfa 'for all users except SAML logged in' option description
         */
        forceExceptSAMLDescription: string;
        /**
         * Cancel editing Mfa policy button text
         */
        cancel: string;
        /**
         * Submit editing Mfa policy button text
         */
        save: string;
    };
    /**
     * Edit Lockout security policy dialog strings
     */
    security_EditLockoutPolicy: {
        /**
         * Lockout Policy Dialog title
         */
        title: string;
        /**
         * Max attempts before lockout user input label
         */
        attemptsInputLabel: string;
        /**
         * Max attempts before lockout user input placeholder
         */
        attemptsInputPlaceholder: string;
        /**
         * Max attempts before lockout user input error message
         */
        attemptsInputErrorMessage: string;
        /**
         * Cancel editing lockout policy button text
         */
        cancel: string;
        /**
         * Submit editing lockout policy button text
         */
        save: string;
    };
    /**
     * Edit Password history security policy dialog strings
     */
    security_EditPasswordHistoryPolicy: {
        /**
         * Password History Policy Dialog title
         */
        title: string;
        /**
         * password history size input label
         */
        sizeInputLabel: string;
        /**
         * password history size input placeholder
         */
        sizeInputPlaceholder: string;
        /**
         * Error message displayed if history size is empty
         */
        sizeIsRequired: string;
        /**
         * Error message displayed if history size must be valid positive number
         */
        sizeMustBeNumber: string;
        /**
         * Error message displayed if history size must be greater than 0
         */
        sizeMustBeGt0: string;
        /**
         * Error message displayed if history size must be less than 10
         */
        sizeMustBelt10: string;
        /**
         * Cancel editing password history policy button text
         */
        cancel: string;
        /**
         * Submit editing password history policy button text
         */
        save: string;
    };
    /**
     * Session management policy section
     */
    security_SessionManagement: {
        /**
         * Session management section title
         */
        sectionTitle: string;
        /**
         * Idle session timeout item title
         */
        idleSessionTimeoutTitle: string;
        /**
         * Idle session timeout item description
         */
        idleSessionTimeoutDescription: string;
        /**
         * Idle session timeout form error
         */
        idleSessionTimeoutErrorMessage: string;
        /**
         * Session timeout item title
         */
        sessionTimeoutTitle: string;
        /**
         * Session timeout item description
         */
        sessionTimeoutDescription: string;
        /**
         * Session timeout form error
         */
        sessionTimeoutErrorMessage: string;
        /**
         * Concurrent sessions item title
         */
        concurrentSessionsTitle: string;
        /**
         * Concurrent sessions item description
         */
        concurrentSessionsDescription: string;
        /**
         * Concurrent sessions timeout form error
         */
        concurrentSessionsTimeoutErrorMessage: string;
        /**
         * Settings `day` option
         */
        day: string;
        /**
         * Settings `days` option
         */
        days: string;
        /**
         * Settings `hour` option
         */
        hour: string;
        /**
         * Settings `hours` option
         */
        hours: string;
        /**
         * Settings `minutes` option
         */
        minute: string;
        /**
         * Settings `minutes` option
         */
        minutes: string;
        /**
         * Save button text
         */
        saveButton: string;
    };
    /**
     * Apply session management policies dialog
     */
    security_SessionManagementDialog: {
        /**
         * Dialog title
         */
        title: string;
        /**
         * Dialog description
         */
        description: string;
        /**
         * Confirm button
         */
        confirm: string;
        /**
         * Cancel button
         */
        cancel: string;
    };
}
